function [latency]=showimage(theImage,theClut,pixelize);% function [latency]=showimage(theImage,theClut,pixelize);%%% make movie	t0=GetSecs; t1=GetSecs;	 % Initialize GetSecs and allocate memory for t0 & t1  	[window,wrect]=SCREEN(0,'OpenWindow',0);	[nr,nc]=size(theImage);		stimrect=SetRect(0,0,nc,nr);	%  newRect = SetRect(left,top,right,bottom);	destrect=stimrect;	destrect=CenterRect(destrect,wrect);		  	offscreen=SCREEN(window,'OpenOffscreenWindow',0,stimrect);	%imax=max(theImage(:)); imin=min(theImage(:));	if (pixelize==1)		dc=mean(theImage(:));		theImage=theImage - dc;		cmax=abs(max(theImage(:)));		cmin=abs(min(theImage(:)));		if (cmax>cmin)			themax=cmax;		else			themax=cmin;		end;		theImage=theImage./themax;		%theImage=scale(theImage);		%theImage=theImage-0.50;		theImage=round(127*theImage+127);	end;	SCREEN(offscreen,'PutImage',theImage);  	% show image		err=SCREEN(window,'SetClut',theClut);	SCREEN(window,'FillRect',127)  	SCREEN('CopyWindow',offscreen,window,stimrect,destrect);	GetSecs(t0);	clicks=MouseClick;	GetSecs(t1);	latency=t1-t0;  	SCREEN('CloseAll'); return;