function [latency]=tileimage(image1,theClut,pixelize);% function [latency]=showimage(image1,theClut,pixelize);%%% make movie	t0=GetSecs; t1=GetSecs;	 % Initialize GetSecs and allocate memory for t0 & t1  	[window,wrect]=SCREEN(0,'OpenWindow',128);	[nr,nc]=size(image1);		stimrect=SetRect(0,0,nc,nr);	%  newRect = SetRect(left,top,right,bottom);	destrect=stimrect;	destrect=CenterRect(destrect,wrect);		  	offscreen=SCREEN(window,'OpenOffscreenWindow',0,stimrect);	imax=max(image1(:)); imin=min(image1(:));	if (pixelize==1)		cmax=abs(max(image1(:)));		cmin=abs(min(image1(:)));		if (cmax>cmin)			themax=cmax;		else			themax=cmin;		end;		image1=image1./themax;		image1=round(127*image1+127);		pt=find(image1>254);		image1(pt)=254+zeros(1,length(pt));		pt=find(image1<0);		image1(pt)=zeros(1,length(pt));	end;	SCREEN(offscreen,'PutImage',image1);  	% show image	if (nargin == 2)		err=SCREEN(window,'SetClut',theClut);	end;	ntr=4;	ntc=4;	xinc=round(1.15*nc);	yinc=round(1.15*nr);	xlimit=wrect(RectRight);	ylimit=wrect(RectBottom);	yval=0;;	while((yval+nr)<ylimit)		destrect=stimrect;		destrect = OffsetRect(destrect,0,yval);		xval=0;		while((xval+nc)<xlimit)  			SCREEN('CopyWindow',offscreen,window,stimrect,destrect);			xval=xval+xinc;			destrect = OffsetRect(destrect,xinc,0)		end;		yval=yval+yinc;	end;			GetSecs(t0);	clicks=MouseClick;	GetSecs(t1);	latency=t1-t0;  	SCREEN('CloseAll'); return;