function csrec=constimInit(values,trialspervalue);%%	function csrec=constimInit(values,trialspervalue);%	values: an array containing the set of stimulus values%	trialpervalue: how many trials per stimulus value?%	vs=size(values);	values=reshape(values,1,vs(1)*vs(2)); % force everything into 1 dimension	vs=size(values);	csrec.values=values;	csrec.numvalues=vs(2);	csrec.trialcount=zeros(size(values));	csrec.numcorrect=zeros(size(values));	csrec.maxtrials=trialspervalue;	csrec.appspec=[];		% applications can use this field to do whatever they want	ci=constimSelect(csrec.numvalues); % select one randomly	csrec.curindex=ci;	% index to current value	csrec.curvalue=csrec.values(ci); % current value	csrec.alldone=0;	csrec.curtrial=1;	csrec.trialdata=[0,0];	csrec.type='constantstimulus';return;	