function [fits,stdev]=dobootstrap(levels,type,reps,threshlev,mu,sigma,gamma,delta);%% function [fits,stdec]=dobootstrap(levels,type,reps,threshlev,mu,sigma,gamma,delta);%% make subject modelsub = subject( type, [ mu sigma ], gamma, delta );% bootstrapfits=[];for i=1:reps,	correct = pollsubject( sub, levels );	[ p1 p2 ] = fitpsymet( [ levels correct ], type );	thresh = getthresh( threshlev, type, [ p1 p2 ], gamma, delta );	fits = [ fits ; p1 p2 thresh ];endstdev = std(fits);return;