function [mu,sigma,threshold,enoughtrials]=psyfuncNormFit(datarec,mintrialforthreshold,muinit,sigmainit,gamma,delta,thresholdlevel);% % %function [mu,sigma,threshold,enoughtrials]=psyfuncNormFit(datarec,mintrialforthreshold,muinit,sigmainit,gamma,delta,thresholdlevel);% % % fit psychometric function to the data	if (sum(datarec.trialcount)>=mintrialforthreshold)		tmpdata=datarec.trialdata;		[mu,sigma]=fitpsymet(tmpdata,'norm',[muinit,sigmainit],gamma,delta);		threshold=getthresh(thresholdlevel,'norm',[mu,sigma],gamma,delta);		disp(['mu = ',num2str(mu),'; sigma = ',num2str(sigma),'; ',num2str(thresholdlevel*100),'% correct threshold = ',num2str(threshold)]);		enoughtrials=1;		else		disp(['WARNING: not enough trials to compute threshold']);		mu=[];		sigma=[];		threshold=[];		enoughtrials=0;		end;return;