function [alpha,beta,threshold,enoughtrials]=psyfuncWeibullFit(datarec,mintrialforthreshold,alphainit,betainit,gamma,delta,thresholdlevel);% % %function [alpha,beta,threshold,enoughtrials]=scWeibullFit(datarec,mintrialforthreshold,alphainit,betainit,gamma,delta,thresholdlevel);% % % fit psychometric function to the data	if (sum(datarec.trialcount)>=mintrialforthreshold)		tmpdata=datarec.trialdata;		[alpha,beta]=fitpsymet(tmpdata,'weibull',[alphainit,betainit],gamma,delta);		threshold=getthresh(thresholdlevel,'weibull',[alpha,beta],gamma,delta);		disp(['alpha = ',num2str(alpha),'; beta = ',num2str(beta),'; ',num2str(thresholdlevel*100),'% correct threshold = ',num2str(threshold)]);		enoughtrials=1;		else		disp(['WARNING: not enough trials to compute threshold']);		alpha=[];		beta=[];		threshold=[];		enoughtrials=0;		end;return;