function finished=scComplete(sc,maxtrials,maxrevs)	switch nargin	case 1		nrevs=sc.maxrevs;		ntrials=sc.maxtrials;	case 2		nrevs=sc.maxrevs;		ntrials=maxtrials;	case 3		nrevs=maxrevs;		ntrials=maxtrials;	end;		[tmp,nSC]=size(sc);	alldone=zeros(1,nSC);	for kk=1:nSC		nr=scReversals(sc(kk));		nt=scTrials(sc(kk));		if (nr>=nrevs)|(nt>=ntrials)			alldone(kk)=1;		end;	end;	finished=min(alldone(:));	return;	