function [r,se]=scReversalavg(sc,lastnrevs)%% function [r,se]=scReversalavg(sc,lastnrevs)%% Return the average and standard error of the last "lastnrevs" reversal points.% If "lastnrevs" is greater than the number of reversals in the staircase, then% "r" and "se" are set to NaN values.%	tmp=sc.reversalcount-lastnrevs+1;	if (tmp<1)|(tmp>sc.reversalcount)		r=NaN;		se=NaN;		return;	end;	revs=sc.revpoints(tmp:sc.reversalcount);	r=mean(revs);	se=std(revs)/sqrt(length(revs));return;