function key = exptpause( winPtr, winRect, flush )	%	% function key = exptpause( winPtr, winRect, flush )	%	%     - <winPtr> is window where message is shown during pause	%     - <winRect> is the rect for winPtr	%	  - <flush> is a parameter that determines if FlushEvents is called		% 13-Apr-98 - created (RFM)	% 20-Feb-02 - added msg parameter (PJB)	% 9-Dec-06	- added flush parameter & changed GetChar to KbCheck (PJB)	% return if window parameters are not passed	if nargin<2,		key=[];		return	end		if (exist('flush')==0)	  flush=0;	end;		WaitSecs(0.1);	if flush	  FlushEvents('keyDown');	end;	ny=10;	% if ~charavail	% 	key=[];	% 	return;	% end;			% set default arguments	% defarg('postsec',1);	% defarg('pausekeys','0');		% show text if key pressed	% key=GetChar;	% if ismember(key,pausekeys),		% pause	% 	[newx,newy] = centertext(winPtr,winRect,'Waiting for a key press...',128,1,24);	mytext=['Please press any key when you are ready to resume...'];	[newx, newy, bbox] = DrawFormattedText(winPtr, mytext, 'center', ny, 0);	Screen('Flip',winPtr);		if flush	  FlushEvents('keyDown');	end;    key=pbGetKey;% 	while ~CharAvail% 		;% 	end;% 	key=GetChar;	if flush	  FlushEvents('keyDown');	end;	% 	[newx,newy] = centertext(winPtr,winRect,'Waiting for a key press...',128,0,24);		% 	WaitSecs(postsec);	% end	WaitSecs(0.1);return