function r=rand1toN(rows,cols,n)%% function r=rand1toN([rows,cols],n)%% returns a rows-by-cols matrix containing random integers between 1 and N% if only 1 argument, X, is passed, then returns a scalar between 1 & X%	if (nargin==1)		n=rows;		rows=1;		cols=1;	end;	if (nargin==2)		error('Only 2 parameters passed to function rand1toN!');	end;			r=1+floor(n*rand(rows,cols));	return;	