function [resp,quitflag]=showandwait(scrn,image,clut)		spacebar=32;	resp = '9';	quitflag=0;		screen(scrn,'SetClut',clut,0);		screen(scrn,'PutImage',image); % this puts the image on the screen.	% wait for space-bar press	flushevents('keyDown');	while abs(resp)~= spacebar & ~isquitkey(resp)		resp = getchar;	end			screen(scrn,'FillRect',0);	% clear the screen with average luminance	if isquitkey(resp)		quitflag=1;	end		return;