function showmessage( winPtr, winRect, mess, txtCLUTindex, bkgCLUTindex)% function showmessage( winPtr, winRect, mess, txtRGB, bkgRGB, mess)%%     - <winPtr> is window where message is shown during pause%     - <winRect> is the rect for winPtr%     - <mess> a string containing the message to be displayed%     - <txtCLUTindex> index to clut used for writing text; default = 1%     - <bkgCLUTindex> index to clut used for erasing text; default = 0% 26-MAR-03 - created (PJB)	if nargin<3, 		return % need both parameters, so return if they are not provided	end	defarg('txtCLUTindex',1);	defarg('bkgCLUTindex',0);	[newx,newy] = centertext(winPtr,winRect,mess,128,txtCLUTindex,24);	[newx,newy] = centertext(winPtr,winRect,'hit any key to continue...',newy+128,txtCLUTindex,24);		flushevents('keyDown');	while ~charavail	end;	key=getchar;	flushevents('keyDown');	[newx,newy] = centertext(winPtr,winRect,mess,128,bkgCLUTindex,24);		[newx,newy] = centertext(winPtr,winRect,'hit any key to continue...',newy+128,bkgCLUTindex,24);	pause(2);return