function takebreak( winPtr, winRect, txtCLUTindex, bkgCLUTindex)% function takebreak( winPtr, winRect, txtRGB, bkgRGB)%%     - <winPtr> is window where message is shown during pause%     - <winRect> is the rect for winPtr%     - <txtCLUTindex> index to clut used for writing text; default = 1%     - <bkgCLUTindex> index to clut used for erasing text; default = 0% 19-FEB-02 - created (PJB)	if nargin<2, 		return % need both parameters, so return if they are not provided	end	defarg('txtCLUTindex',1);	defarg('bkgCLUTindex',0);	[newx,newy] = centertext(winPtr,winRect,'Take a break. Press any key to continue.',128,txtCLUTindex,24);	flushevents('keyDown');	while ~charavail	end;	key=getchar;	flushevents('keyDown');	[newx,newy] = centertext(winPtr,winRect,'Take a break. Press any key to continue.',128,bkgCLUTindex,24);		pause(2);return