% the stimulus & isi durations,in secondsmainscrs = 1;framerate=75;	% frame-rate of the monitor in Hertzisidur=0.1;	% isi in secondsisiframes=round(framerate*isidur);tempCLUT=ones(256,3)*160;[screens,rects] = openscreens(mainscrs,tempCLUT);% These values work for Big Blue's monitor... They are hardware dependent!!!screen(screens(mainscrs),'Preference','WaitForVBLInterrupt',1);screen(screens(mainscrs),'Preference','SetClutWaitVBL',1);kk=100;tocdata=zeros(1,kk);for n=1:kk	tic;	screen(screens(mainscrs),'WaitVBL',isiframes);	tocdata(n)=toc;end;closeall;disp(['mean seconds = ',num2str(mean(tocdata))]);