function [mousefilename]=pmcreatemousefile(mousedatadir,initials,sidnum,exptname,sgender,sage,scomment,mainpath);% create filename for mouse datacd(mousedatadir);dt=dir; tmp=size(dt); kk=tmp(1);found=1;k=1;while found	mousefname=[initials,'pmMouse','_ID',num2str(sidnum),'_N',num2str(k)];	testname=[mousefname,'.txt'];	tmp=0;	for n=1:kk		tmp(n)=strcmp(lower(testname),lower(dt(n).name));	end;	found=max(tmp);	k=k+1;end;mousefilename=testname;% now write the header to the filethedate=date;mousefileID=fopen(mousefilename,'a');count=fprintf(mousefileID,'%s\n',['Date: ',thedate]);count=fprintf(mousefileID,'%s\n',['File: ',mousefilename]);count=fprintf(mousefileID,'%s\n',['Experiment: ',exptname]);count=fprintf(mousefileID,'%s\n',['Subject ID: ',sidnum]);count=fprintf(mousefileID,'%s\n',['Gender: ',sgender]);count=fprintf(mousefileID,'%s\n',['Age: ',num2str(sage)]);count=fprintf(mousefileID,'%s\n\n',['Comment: ',scomment]);count=fprintf(mousefileID,'%s\n\n',['Comment: ',scomment]);count=fprintf(mousefileID,'%s \t %s \t %s \t %s \t %s \t %s \t %s\n','Trial #','Time (sec)','x pos','y pos','distance','angle','button');fclose(mousefileID);cd(mainpath);return;