conds=unique(data(:,3)); % get a list of the ISIs used in this experiment (i.e., stimOffset-moveMouse ISI)theta=mod(data(:,1)+data(:,2),360); % theta is the direction of the stimulus on every trialangledifference=theta-data(:,5); % this is the difference between stimulus and response directions on every trialzcriterion=2.57; % we will use this to clip outliers; anything beyond this # of standard deviations will be removeddisp(['isi(frames); median(error); avg(error); sd(error); SE(sd)']);% calculate stats for each ISI (collapsing across stimulus directions)for kk=1:length(conds)	tmp=find(data(:,3)==conds(kk)); % extract trials with ISI=conds(kk)	cdata=angledifference(tmp); % extact the "errors" for this condition	clippeddata=zscoreclipdata(cdata,zcriterion); % create a "clipped" data set by throwing out outliers	mediandata(kk)=median(cdata); % compute the median on the raw data	medianclipped(kk)=median(clippeddata); % compute the median on the clipped data	avgdata(kk)=mean(cdata); % compute the mean	avgclipped(kk)=mean(clippeddata); % compute mean on clipped data	sddata(kk)=sqrt(var(cdata)); % compute SD of errors	sesddata(kk)=samperrsd(sddata(kk),length(cdata)); % estimate the standard error of the sample SD	sdclipped(kk)=sqrt(var(clippeddata));  % compute SD of clipped errors	sesdclipped(kk)=samperrsd(sdclipped(kk),length(clippeddata)); % estimate the standard error of the clipped sample's SD	disp([conds(kk) mediandata(kk) avgdata(kk) sddata(kk) sesddata(kk)]); % display the results (raw data set) on the command windowend;disp(' ');disp(['after points in each condition beyond ±', num2str(zcriterion),' standard deviations are removed...']);disp(['isi(frames); median(error); avg(error); sd(error); SE(sd)']);for kk=1:length(conds)	disp([conds(kk) medianclipped(kk) avgclipped(kk) sdclipped(kk) sesdclipped(kk)]);end;