% file & directory names namesdefarg('datafilename',['testdata.txt']);defarg('mousedatafilename',['mousedata.txt']);defarg('pmMOUSEfolder',['pmMOUSEfiles']);	% N.B. make sure this folder exists within the folder containing 'pmlab'!defarg('pmTEXTfolder',['pmTEXTfiles']);	% N.B. make sure this folder exists within the folder containing 'pmlab'!defarg('pmMATfolder',['pmMATfiles']);	% N.B. make sure this folder exists within the folder containing 'pmlab'!% set the pathsdisp('setting path names...');mfilename='pmlab'; mainfile = [mfilename,'.m'];eval(['mainpath=which(',quotestring(mainfile),');']);mainpath = mainpath(1:end-length(mainfile));matdatadir=[mainpath,pmMATfolder,':'];mousedatadir=[mainpath,pmMOUSEfolder,':'];textdatadir=[mainpath,pmTEXTfolder,':'];cd(mainpath);