m=0;sd=[1,2,4,8,16,32,64,128];n=10;csd=1+(1/(4*(n-1))); % sample sd is biased estimate of population sd; this is the correction factor listed by Hays (191981) p 189.for kk=1:length(sd)	tmp=normrnd(m,sd(kk),n,10000); % 10,000 simulated data sets; each data set is represented by a single column	sampsd=sqrt(var(tmp)); % compute the sd for each data set (i.e., each column)	sampsd=sampsd.*csd; % correct for the bias	msd(kk)=mean(sampsd); % compute the mean of the sampling distribution	sesd(kk)=sqrt(var(sampsd)); %compute the sd of the sampling distributionend;figID=1;% figure(figID); plot(sd,msd);figID=figID+1;% figure(figID); plot(sd,sesd);figID=figID+1;% figure(figID); plot(sd,(sesd./sd));figID=figID+1;disp(['n = ',num2str(n)]);disp([sd' msd' sesd' (sesd./sd)']);clear msd sesd;sd=1;n=[2,5,10,20,50,100,200];X=1;20;for kk=1:length(n)	tmp=normrnd(m,sd,n(kk),10000); % 10,000 simulated data sets; each data set is represented by a single column	sampsd=sqrt(var(tmp)); % compute the sd for each data set (i.e., each column)	sampsd=sampsd.*csd; % correct for the bias	msd(kk)=mean(sampsd); % compute the mean of the sampling distribution	sesd(kk)=sqrt(var(sampsd)); %compute the sd of the sampling distribution% 	figure(figID);plot(x,hist(sampsd,x));% 	figID=figID+1;end;% figure(figID); plot(n,msd);figID=figID+1;figure(figID); plot(n,sesd);figID=figID+1;figure(figID); plot(n,(sesd./sd));figID=figID+1;disp(['sd = ',num2str(sd)]);disp([n' msd' sesd' (sesd./sd)']);