function [calibration,screens,offscrPtrs,offscrCluts,OnScreenRect,clut] = OpenScreenz(mainscrs)% OPENSCREENS opens the main screen and 3 off-screen windows.% % mainscrs = the number of the screen to be used as the main screen (i.e. 1 or 0).% % e.g.% [calibration,screens,offscrPtrs,offscrCluts,rect] = OpenScreens(0)%Removes cursor from screenhidecursor;hidecursor;%calibrate monitorcalibration = getddf('sample.ddf');% make a dummy clutclut = [0:255]'*[1 1 1];clut(1,:) = [160 160 160];% specifies the colour of the text for messagesmsgcolor = 160;mainscrs = 0;% open main the screen[screens,OnScreenRect] = openscreens(mainscrs,clut);	%Sets clut on mainscrsscreen(screens(1),'SetClut',clut,0);% open the off-screen windows	numwindows = 3;	offscrptrs=zeros(numwindows,1);	for i=1:numwindows		offscrPtrs(i) = SCREEN(mainscrs,'OpenOffscreenWindow',0);		offscrCluts{i} = zeros(256,3);	endreturn