fid=fopen('Shapes','wt+') % Opens the filewindow=SCREEN(0, 'OpenWindow'); % Open the screen[Offscreen,Offrect]=SCREEN(0,'OpenOffscreenWindow'); % Open off screen window corrsnd=makesnd(600,.09,.65); % Creates sound filerect = SCREEN(window, 'Rect');   % Define size of screencolor=1; % Defines colorcirclerect= [rect(3)/2-100 rect(4)/2-100 rect(3)/2+100 rect(4)/2+100];  % Define size/placement circlesquareRect= [rect(3)/2-100 rect(4)/2-100 rect(3)/2+100 rect(4)/2+100]; % Define size/placement of squareoval= [rect(3)/2-100 rect(4)/2-150 rect(3)/2+100 rect(4)/2+150]; % Define size/placement ovaltriangle=[rect(3)/2-100 rect(4)/2+50; rect(3)/2 rect(4)/2-75; rect(3)/2+100 rect(4)/2+50]; % Define triangle coordinates% Create an array to randomize display of shapesshapes = [1 1 1 2 2 2 3 3 3 4 4 4]ranshapesindex=randperm(12)for i=1:12	ranshapes(1,i)=shapes(1,ranshapesindex(1,i));end ranshapes % Random arrayx=1for x=1:12		 n=ranshapes(x)  if n==1 	  SCREEN(window, 'FillOval',color,circlerect) % Draw circle  elseif n==2 	  SCREEN(window, 'FillRect',color,squareRect) % Draw Square  elseif n==3	  SCREEN(window, 'FillOval',color,oval) % Draw oval  elseif n==4	  SCREEN(window, 'FillPoly',color,triangle) % Draw triangle  end a=getchar if a=='1'	 count=fprintf(fid,'%s\n','Response : circle'); elseif a=='2'	 count=fprintf(fid,'%s\n','Response : square'); elseif a=='3'	 count=fprintf(fid,'%s\n','Response : oval'); elseif a=='4'	 count=fprintf(fid,'%s\n','Response : triangle'); end SCREEN('CopyWindow',Offscreen,window) % Clear screen x=x+1;endca  