color=1;%blackwindow=SCREEN(0,'OpenWindow');%opens window% rect = [0 0 800 600]% rect = [Left Top Right Bottom][clearscreen,clearrect]=SCREEN(0,'OpenOffscreenWindow')rect = SCREEN(window,'Rect')circle=[rect(3)/2-100 rect(4)/2-100 rect(3)/2+100 rect(4)/2+100]% define circle sizesquareRect=[rect(3)/2-100 rect(4)/2-100 rect(3)/2+100 rect(4)/2+100]%define square sizeoval=[rect(3)/2-100 rect(4)/2-150 rect(3)/2+100 rect(4)/2+150]%define oval sizetriangle=[rect(3)/2 rect(4)/2; rect(3)/2-200 rect(4)/2-100; rect(3)/2+200 rect(4)/2-100]SCREEN(window,'FillOval',circle)%draw circlegetchar;% wait for keypressSCREEN('CopyWindow',clearscreen,window)SCREEN(window,'FillRect',squareRect)%draw squaregetchar;%wait for keypressSCREEN('CopyWindow',clearscreen,window)SCREEN(window,'FillOval',oval)%draw ovalgetchar;SCREEN('CopyWindow',clearscreen,window)SCREEN(window,'FillPoly',triangle)getchar;SCREEN('CopyWindow',clearscreen,window)ca