color=0;window=SCREEN(0,'OpenWindow');% rect = [0 0 800 600]% rect = [Left Top Right Bottom]rect = SCREEN(window,'Rect')squareRect= [rect(3)/2-100 rect(4)/2-100 500 400]%set size of square w/. re. to coordinates[oldFontName,oldFontNumber]=SCREEN(window,'TextFont',4);%define fontoldFontSize=SCREEN(window,'TextSize',46)%define fontsizewidth=SCREEN(window,'TextWidth','square');	% horizontal offset (negative for right-to-left scripts, e.g. Hebrew).[newX,newY] = SCREEN(window,'DrawText','square',rect(3)/2-width/2,squareRect(4)+50)%places text in the center of the screen beneath square SCREEN(window,'FillRect',color,squareRect)%draws squarekbwait;% waits for keypressca