beep=makesnd(200,.10,.6);color=1;%blackwindow=SCREEN(0,'OpenWindow');%opens window% rect = [0 0 800 600]% rect = [Left Top Right Bottom][clearscreen,clearrect]=SCREEN(0,'OpenOffscreenWindow')rect = SCREEN(window,'Rect')circle= [rect(3)/2-100 rect(4)/2-100 rect(3)/2+100 rect(4)/2+100]% define circle sizesquareRect=[rect(3)/2-100 rect(4)/2-100 rect(3)/2+100 rect(4)/2+100]%define square sizeoval=[rect(3)/2-100 rect(4)/2-150 rect(3)/2+100 rect(4)/2+150]%define oval sizetriangle=[rect(3)/2 rect(4)/2; rect(3)/2-200 rect(4)/2-100; rect(3)/2+200 rect(4)/2-100]x=0for x = 1:12	n= randomcondition(4)		if n==1		SCREEN(window,'FillOval',[color],circle)%draw circle		getchar;		sound(beep)		SCREEN('CopyWindow',clearscreen,window)		elseif n==2			SCREEN(window,'FillRect',color,squareRect)%draw square			getchar;			sound(beep)			SCREEN('CopyWindow',clearscreen,window)			elseif n==3				SCREEN(window,'FillOval',[color],oval)%draw oval				getchar;				sound(beep)				SCREEN('CopyWindow',clearscreen,window)				elseif n==4					SCREEN(window,'FillPoly',[color],triangle)					getchar;					sound(beep)					SCREEN('CopyWindow',clearscreen,window)				end								x = x+1			end		ca