% Find the calibration filecalibration=getddf('william.ddf');% Set the contrast for the circleContrast=0.5;% Make the clutclut=[1:256]'*[1,1,1]; % matrix of colors% Open the on-screen windowwindow=SCREEN(0,'OpenWindow');% Get the rect for the on-screen windowrect=SCREEN(window,'Rect');% Set the clutSCREEN(window,'SetClut',clut);% Set the circle colourcolor=1; % Defines color% Open the off-screen window[Offscreen,Offrect]=SCREEN(0,'OpenOffscreenWindow'); % Opens offscreen window% Define the rectangle that the circle will be inscribed incirclerect= [rect(3)/2-100 rect(4)/2-100 rect(3)/2+100 rect(4)/2+100];  % Define size/placement circle% Draw a circle on the off-screen window% SCREEN(Offscreen, 'FillOval',color,circlerect) % Draw circleSCREEN(Offscreen,'FillOval',color,circlerect)	% Gets the circle matrix from the offscreen window	imageMatrix=screen(Offscreen,'GetImage',circlerect);		% Converts imgeMatrix to a double precision array.	imageMatrix=double(imageMatrix);		% Multiplies the imageMatrix by -1.	imageMatrix=imageMatrix.*-1;		% Scales the extracted image so that its contrast values are between -1 and 0.	newimage = scalearb(imageMatrix,-1,0);% 	newimage=scale(imageMatrix)-1;			% Converts all non-zero elements of the imageMatrix to -1.	locs = find(imageMatrix~=0);	imageMatrix(locs) = -1;		% Sets the contrast on the circle to that specified by "Contrast."	imageMatrix=imageMatrix.*Contrast;				% converts the stimulus matrix into luminance values.	[stimulus,clut] = makeimage(imageMatrix,calibration);		% Sets the Colour Look Up Table on the main screen.	SCREEN(window,'SetClut',clut);	% Present the stimulus	screen(window,'PutImage',stimulus);		% Wait for a characer press	kbwait;		% Clear the screen	ca;