function displayTextures(curStimVariance, nzvar)load something;image=something.nz1;facestim=image*sqrt(1/(std(std(image))^2));cmax=1.6413;cmin=-.9348;    %nz=randn([256,256])*(sqrt(nzvar)); jesse's noise    [stim,nzseed]=noise2d(256,.1,cmin,cmax,0);	noiseSD2=std2(stim)    noiseSD=std(stim(:))	curStimVariance=0.0290; % get the stimulus variance for the current method-of-constant-stimuli record	tmpface=facestim*sqrt(curStimVariance); % this formula works because the faces have a variance of 1	stim=stim+tmpface;	%stim=tmpface+nz; jesse's noise adding method			figure	imshow(imscale(stim));	save zstimulus stim	