tmpim=rand(4,4);tmpim=(tmpim-mean(tmpim(:)))./mean(tmpim(:));[newim,newclut]=makeimage(tmpim,cal);ddf_file=cal.calmatrix;numrow=size(newim,1);numcol=size(newim,2);for r=1:numrow	for c=1:numcol		pixIndex=newim(r,c)		clutrow(1,:)=newclut(pixIndex,:)		ddfrow=find(ddf_file(:,1)==clutrow(1) & ddf_file(:,2)==clutrow(2) & ddf_file(:,3)==clutrow(3))		lumim(r,c)=ddf_file(ddfrow,4)	endendtmpimnewimlumim=(lumim-mean(lumim(:)))/mean(lumim(:))