load invertedA.mat;facenames={'female1';'male1';'female2';'male2';'female3';'male3';'female4';'male4';'female5';'male5'};[numfaces,kk]=size(facenames);for kk=1:numfaces	facestim(kk).face=eval(['invertedA.',char(facenames(kk))]);	facestim(kk).face=facestim(kk).face*sqrt(1/var(facestim(kk).face(:))); % set variance to 1end;f=1;corrMatrix=zeros(10,10);for row=1:10	for col=1:10		correlation=corrcoef(facestim(row).face, facestim(col).face);			corrMatrix(row,col)=correlation(1,2);	endend			