% external noise variancesnv=[0.001,0.01,0.1];				% external noise variances (low, medium, & high)numnz=length(nv);	% number of external noises% parameters for method of constant stimulidefarg('numvalues',1); % # of stimulus levels (i.e., contrast variance) used in each conditiondefarg('trialspervalue',[1,20,40]); % # of trials per contrast leveldefarg('stepsperlogunit',10);% defarg('thresholdguess',[0.00001,0.0015,0.003]);defarg('thresholdguess',3*nv/40); % these seem to be about rightfor kk=1:numnz	tmp=thresholdguess(kk)/sqrt(10);	values(kk,:)=logspace(log10(tmp),log10(10*tmp),numvalues); % set (n=numvalues) of log-spaced values centered on thresholdguess	constimrec(kk)=constimInit(values(kk,:),trialspervalue(1)); % init the constantstimulus struct	constimrec(kk).appspec=nv(kk);	% attached noise variance to app-specific fieldend;